package entryTicket;

public class EntryTicketPractice2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 29 % 10;
		if (x > 10)
		{
		System.out.println(1);
		}
		else if (x > 8)
		{
		System.out.println(2);
		}
		else if (x > 6)
		{
		System.out.println(3);
		}
		else if (x > 4)
		{
		System.out.println(4);
		}
		else
		{
		System.out.println(5);
		}
	}

}
